INSERT INTO `project` VALUES (19, '空-天-地-内一体化地质灾害探（观）测技术研究', '663', '针对四川省强震作用持续影响、人类工程活动不断加剧、极端天气频繁出现、常规巡查手段力所不及等因素造成的地质灾害高发、频发问题，研究基于卫星InSAR的大面积地质灾害普查和变形重点区域分析技术，基于无人机倾斜摄影、机载LiDAR的灾害体形变特征的早期识别与成灾前兆信息的地质灾害隐患详查和疑似灾害点快速识别技术，基于地基InSAR、地面LiDAR、近景摄影测量及便携式地下（内   ）高精度监测等技术的地质灾害隐患精细核查和精细化判识技术，最终形成卫星InSAR、无人机LIDAR、无人机数字摄影测量、GBSAR、地面三维激光扫描、近景摄影测量和现场快速监测的空-天-地-内一体化的潜在大型滑坡隐患早期识别方法和成果，达到主动防范地质灾害的目的，有效避免重大灾害性事件发生，最大程度减少人民生命财产损失。', 4, '150000', '2018-01-01 00:00:00', NULL, '2018-01-01 00:00:00', '2020-12-31 00:00:00', '进行中', '2018-03-06 15:06:55', '2018-03-06 15:06:55');
